mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, title, contents, viewCount, imagepath, regdate FROM board
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE board
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE board
SET title=#{title}, content=#{content}
WHERE num=#{num}
